import {
  Button,
  Flex,
  Typography,
  Form,
  FormProps,
  Image,
  notification,
} from "antd";
import { UserOutlined } from "@ant-design/icons";
import Input from "antd/es/input/Input";
import { Link, useNavigate } from "react-router";
import logo from "../assets/logo.webp";
import CarouselCompo from "../components/CarouselComponent";
import { useAuth } from "../components/AuthContext";
import { Login } from "../controllers/SessionsController";
import { LoginInterface } from "../models/SessionsModel";

const { Title, Text } = Typography;

type FieldType = {
  email?: string;
  password?: string;
  remember?: string;
};

function LoginView() {
  const [form] = Form.useForm();
  const [api, contextHolder] = notification.useNotification();
  const { login } = useAuth();

  const navigate = useNavigate();
  const onFinish: FormProps<FieldType>["onFinish"] = (values) => {
    const registrar = async () => {
      try {
        const loginData: LoginInterface = {
          email: values.email ? values.email : "",
          password: values.password ? values.password : "",
        };

        const data = await Login(loginData);
        api.success({
          message: "Registro exitoso",
          description: "Su Usuario ha sido creado exitosamente.",
        });
        login(data.token);
        setTimeout(() => {
          navigate("/dashboard");
        }, 2000);
        form.resetFields();
      } catch (error) {
        api.error({
          message: "Error en el registro",
          description:
            "Hubo un problema al crear su Usuario. Por favor, intente nuevamente.",
        });
      }
    };
    registrar();
  };

  const onFinishFailed: FormProps<FieldType>["onFinishFailed"] = (
    errorInfo
  ) => {
    console.log("Fallido:", errorInfo);
  };

  return (
    <>
      {contextHolder}
      <Flex vertical={false}>
        <Flex
          style={{ width: "50%", height: "100vh" }}
          vertical={true}
          justify="center"
        >
          <CarouselCompo />
        </Flex>
        <Form
          onFinish={onFinish}
          onFinishFailed={onFinishFailed}
          name="login"
          style={{
            gap: "8px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            flexDirection: "column",
            width: "50%",
            height: "100vh",
          }}
        >
          <Image
            preview={false}
            width={200}
            height={200}
            src={logo}
            fallback="data:image/png;base64,"
          />
          <Title>COMUNAS</Title>
          <Flex vertical style={{ width: "80%" }} gap="small">
            <Form.Item<FieldType>
              name="email"
              rules={[
                { required: true, message: "Por favor, ingresa tu Correo!" },
              ]}
            >
              <Input placeholder="Ingresar Correo" suffix={<UserOutlined />} />
            </Form.Item>
            <Form.Item<FieldType>
              name="password"
              rules={[
                { required: true, message: "Por favor, ingresa tu Correo!" },
              ]}
            >
              <Input.Password placeholder="Ingresar Contraseña" />
            </Form.Item>
          </Flex>
          <Flex
            vertical={false}
            gap="small"
            style={{ width: "80%" }}
            justify="center"
          >
            <Form.Item style={{ width: "80%" }}>
              <Button
                type="primary"
                htmlType="submit"
                style={{ width: "100%" }}
              >
                Ingresar
              </Button>
            </Form.Item>
          </Flex>
          <Text>
            ¿Desea crear una cuenta?
            <Link to="/signin"> ¡Crea una!</Link>
          </Text>
        </Form>
      </Flex>
    </>
  );
}

export default LoginView;
