import { Button, Flex, Typography, Image, Form, notification } from "antd";
import {
  UserOutlined,
  MailOutlined,
  EyeInvisibleOutlined,
  EyeOutlined,
} from "@ant-design/icons";
import Input from "antd/es/input/Input";
import { Link } from "react-router";
import logo from "../assets/logo.webp";
import CarouselCompo from "../components/CarouselComponent";
import { Register } from "../controllers/SessionsController";
import { useNavigate } from "react-router";
import { useAuth } from "../components/AuthContext";
import { useState } from "react";
const { Title, Text } = Typography;

function SignIn() {
  const [form] = Form.useForm();
  const [api, contextHolder] = notification.useNotification();
  const [loading, setLoading] = useState<boolean>(false);
  const { login } = useAuth();
  const [showPassword, setShowPassword] = useState(true);

  const navigate = useNavigate();
  const onFinish = (values: any) => {
    const registrar = async () => {
      try {
        setLoading(true);
        const data = await Register(values);
        api.success({
          message: "¡Registro exitoso!",
          description: "¡Su Usuario ha sido creado exitosamente!",
        });
        login(data.token);
        navigate("/dashboard");
        form.resetFields();
      } catch (error) {
        setLoading(false);
        api.error({
          message: "Error en el registro.",
          description:
            "¡Hubo un problema al crear su Usuario. Por favor, intente nuevamente!",
        });
      }
    };
    registrar();
  };

  const onFinishFailed = (errorInfo: any) => {
    console.log("Failed:", errorInfo);
  };

  return (
    <>
      {contextHolder}
      <Flex vertical={false}>
        <Flex
          style={{ width: "50%", height: "100vh" }}
          vertical={true}
          justify="center"
        >
          <CarouselCompo />
        </Flex>
        <Flex
          gap="middle"
          vertical
          justify="center"
          align="center"
          style={{ width: "50%", height: "100vh" }}
        >
          <Image
            preview={false}
            width={200}
            height={200}
            src={logo}
            fallback="data:image/png;base64,"
          />
          <Title>COMUNAS</Title>
          <Form
            form={form}
            name="sign_in"
            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
            style={{ width: "80%" }}
          >
            <Form.Item
              name="username"
              rules={[
                { required: true, message: "¡Por favor, ingrese su Usuario!" },
              ]}
            >
              <Input placeholder="Usuario" suffix={<UserOutlined />} />
            </Form.Item>
            <Form.Item
              name="email"
              rules={[
                {
                  required: true,
                  message: "¡Por favor, ingrese su Correo Electrónico!",
                },
              ]}
            >
              <Input
                placeholder="Correo Electrónico"
                suffix={<MailOutlined />}
              />
            </Form.Item>
            <Form.Item
              name="password"
              rules={[
                {
                  required: true,
                  message: "¡Por favor, ingrese su Contraseña!",
                },
              ]}
            >
              <div style={{ position: "relative" }}>
                <Input
                  type={showPassword ? "password" : "text"}
                  placeholder="Ingresa tu Contraseña"
                />
                <Button
                  type="text"
                  onClick={() => setShowPassword((prev) => !prev)}
                  style={{
                    position: "absolute",
                    right: 10,
                    top: "50%",
                    transform: "translateY(-50%)",
                  }}
                  icon={
                    showPassword ? <EyeInvisibleOutlined /> : <EyeOutlined />
                  }
                />
              </div>
            </Form.Item>
            <Form.Item>
              <Button
                loading={loading}
                type="primary"
                htmlType="submit"
                style={{ width: "100%" }}
              >
                Crear Usuario
              </Button>
            </Form.Item>
          </Form>
          <Text>
            ¿Ya tiene un Usuario? <Link to="/login"> Iniciar sesión</Link>
          </Text>
        </Flex>
      </Flex>
    </>
  );
}

export default SignIn;
